# -*- coding: utf-8 -*-
"""
Decodes a raw Pronto IR hex dump into binary and hexadecimal representations.

This script is designed to analyze the mark/space timings typical of IR protocols,
especially complex ones like those used for air conditioners.
"""

def analyze_pronto_dump(pronto_data):
    """
    Parses a Pronto IR dump, decodes it to binary, and converts it to hex.

    Args:
        pronto_data (str): A multi-line string containing the Pronto hex codes.

    Returns:
        str: A formatted string with the analysis results for each frame.
    """
    # 1. Clean and parse the input data
    # Combine lines, split into individual hex codes, and convert to integers.
    codes_str = pronto_data.strip().replace('\n', ' ').split()
    try:
        codes_int = [int(code, 16) for code in codes_str]
    except ValueError as e:
        return f"Error: Invalid hex code found in input data. {e}"

    # 2. Remove the standard Pronto header (4 words)
    # The header is: 0000 (raw) <freq_code> <seq1_len> <seq2_len>
    if len(codes_int) < 4:
        return "Error: Data is too short to be a valid Pronto dump."
    
    # The actual timing data starts after the header.
    timing_data = codes_int[4:]

    # 3. Define thresholds for decoding
    # A short space represents a '0', a long space represents a '1'.
    # A very long mark indicates a leader pulse, which separates frames.
    BIT_ZERO_UPPER_THRESHOLD = 0x2A      # Space values below this are '0'
    BIT_ONE_UPPER_THRESHOLD = 0x80       # Space values between the thresholds are '1'
    LEADER_MARK_LOWER_THRESHOLD = 0x80   # Mark values above this indicate a leader pulse

    frames = []
    current_frame_bits = ""
    
    # 4. Iterate through the mark/space pairs to decode bits
    # We look for long marks to identify frame separators/leaders.
    for i in range(0, len(timing_data), 2):
        # Ensure we have a full mark/space pair to process
        if i + 1 >= len(timing_data):
            break
            
        mark_duration = timing_data[i]
        space_duration = timing_data[i+1]

        # A long mark indicates a leader pulse, which separates frames.
        if mark_duration > LEADER_MARK_LOWER_THRESHOLD:
            # If we have accumulated bits for a frame, save it.
            # This check prevents saving an empty string on the very first leader pulse.
            if current_frame_bits:
                frames.append(current_frame_bits)
            
            # Reset for the next frame's data.
            current_frame_bits = ""
            # The leader pulse itself is not data, so we skip to the next pair.
            continue

        # If it's not a leader pulse, it's a data bit. Decode based on space duration.
        if space_duration < BIT_ZERO_UPPER_THRESHOLD:
            current_frame_bits += '0'
        elif space_duration < BIT_ONE_UPPER_THRESHOLD:
            current_frame_bits += '1'

    # After the loop, add the last captured frame if it exists.
    if current_frame_bits:
        frames.append(current_frame_bits)

    # 5. Format the output
    output = "--- Pronto IR Analysis ---\n\n"
    if not frames:
        return "No valid IR data frames were found in the provided dump."

    for i, frame_bits in enumerate(frames):
        frame_num = i + 1
        bit_count = len(frame_bits)
        
        output += f"## Frame {frame_num} ({bit_count} bits)\n"
        output += f"Binary: {frame_bits}\n"

        # Convert binary string to hexadecimal
        try:
            # Pad the binary string with leading zeros to make its length a multiple of 8
            # for correct conversion to bytes.
            num_bytes = (bit_count + 7) // 8
            padded_bits = frame_bits.zfill(num_bytes * 8)
            
            # Convert the padded binary string to an integer, then to a hex string
            hex_value = f'{int(padded_bits, 2):X}'
            
            # Ensure the hex string has the correct number of characters (2 per byte)
            hex_value = hex_value.zfill(num_bytes * 2)

            # Add spaces between bytes for readability
            hex_formatted = ' '.join(hex_value[j:j+2] for j in range(0, len(hex_value), 2))
            output += f"Hex:    {hex_formatted}\n\n"

        except Exception as e:
            output += f"Hex:    Error converting to hex: {e}\n\n"
            
    return output

